// App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useAuth } from './hooks/useAuth';
import Header from './components/Layout/Header';
import AuthModal from './components/Auth/AuthModal';
import SplashCursor from './components/Layout/SplashCursor'; 
import HomePage from './pages/HomePage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import UploadPrescriptionPage from './pages/UploadPrescriptionPage';
import AdminDashboardPage from './pages/AdminDashboardPage';
import OrderHistoryPage from './pages/OrderHistoryPage';
import ProtectedRoute from './components/Common/ProtectedRoute';
import './App.css';

function App() {
  const [showLoginPopup, setShowLoginPopup] = useState(false);
  const [showCursor, setShowCursor] = useState(true); // ← Cursor kontrolü için state
  const { isAuthenticated } = useAuth();

  useEffect(() => {
    const hasSeenPopup = sessionStorage.getItem('seenLoginPopup');
    if (!isAuthenticated && !hasSeenPopup) {
      setShowLoginPopup(true);
      sessionStorage.setItem('seenLoginPopup', 'true');
    }

    // Mobile cihaz kontrolü
    const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
    if (isMobile) {
      setShowCursor(false);
    }
  }, [isAuthenticated]);

  return (
    <div className="app-container">
      {/* Fluid Cursor'ı buraya ekleyin */}
      {showCursor && (
        <SplashCursor 
          SPLAT_RADIUS={0.25}
          SPLAT_FORCE={7000}
          BACK_COLOR={{ r: 0.05, g: 0.05, b: 0.1 }}
          COLOR_UPDATE_SPEED={12}
        />
      )}
      
      <Header onLoginClick={() => setShowLoginPopup(true)} />
      {showLoginPopup && <AuthModal onClose={() => setShowLoginPopup(false)} />}
      
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/cart" element={<CartPage />} />
        
        {/* Protected Routes */}
        <Route 
          path="/checkout" 
          element={<ProtectedRoute><CheckoutPage /></ProtectedRoute>} 
        />
        <Route 
          path="/upload-prescription" 
          element={<ProtectedRoute><UploadPrescriptionPage /></ProtectedRoute>} 
        />
        <Route 
          path="/my-orders" 
          element={<ProtectedRoute><OrderHistoryPage /></ProtectedRoute>} 
        />
        <Route 
          path="/admin" 
          element={<ProtectedRoute adminOnly={true}><AdminDashboardPage /></ProtectedRoute>} 
        />
      </Routes>
    </div>
  );
}

export default App;
