import React, { useEffect, useRef } from 'react';

function SplashCursor() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Canvas boyutunu ayarla
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    const ctx = canvas.getContext('2d');
    let mouseX = window.innerWidth / 2;
    let mouseY = window.innerHeight / 2;
    let particles = [];
    let trails = []; // Mouse izleri

    const handleMouseMove = (e) => {
      mouseX = e.clientX;
      mouseY = e.clientY;
      
      // Mouse izi ekle
      trails.push({ x: mouseX, y: mouseY, life: 20 });
      
      // Yumuşak partikül ekle
      particles.push({
        x: mouseX,
        y: mouseY,
        size: Math.random() * 3 + 1,
        speedX: Math.random() * 4 - 2,
        speedY: Math.random() * 4 - 2,
        color: `hsl(${Math.random() * 60 + 200}, 70%, 60%)`, // Mavi tonları
        life: 30
      });
    };

    const animate = () => {
      // ÇOK HAFİF arkaplan temizleme - neredeyse saydam
      ctx.fillStyle = 'rgba(0, 0, 0, 0.02)'; // Çok daha açık
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Mouse izlerini çiz (çok hafif)
      for (let i = 0; i < trails.length; i++) {
        const trail = trails[i];
        const alpha = trail.life / 20 * 0.1; // Çok düşük opaklık
        ctx.fillStyle = `rgba(100, 150, 255, ${alpha})`;
        ctx.beginPath();
        ctx.arc(trail.x, trail.y, 2, 0, Math.PI * 2);
        ctx.fill();
        
        trail.life--;
        if (trail.life <= 0) {
          trails.splice(i, 1);
          i--;
        }
      }

      // Ana cursor - daha minimalist
      ctx.fillStyle = 'rgba(74, 144, 226, 0.9)'; // Açık mavi
      ctx.beginPath();
      ctx.arc(mouseX, mouseY, 6, 0, Math.PI * 2);
      ctx.fill();

      ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
      ctx.beginPath();
      ctx.arc(mouseX, mouseY, 2, 0, Math.PI * 2);
      ctx.fill();

      // Partikülleri çiz ve güncelle
      for (let i = 0; i < particles.length; i++) {
        const p = particles[i];
        
        // Yavaş ve smooth hareket
        p.x += p.speedX * 0.5;
        p.y += p.speedY * 0.5;
        p.life--;

        const alpha = p.life / 30 * 0.6; // Düşük opaklık
        ctx.fillStyle = p.color.replace(')', `, ${alpha})`).replace('hsl', 'hsla');
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
        ctx.fill();

        if (p.life <= 0) {
          particles.splice(i, 1);
          i--;
        }
      }

      // Daha az partikül için sınırlama
      if (particles.length > 50) {
        particles = particles.slice(-50);
      }

      requestAnimationFrame(animate);
    };

    window.addEventListener('mousemove', handleMouseMove);
    animate();

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', resizeCanvas);
    };
  }, []);

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      zIndex: 9999,
      pointerEvents: 'none',
      width: '100%',
      height: '100%'
    }}>
      <canvas 
        ref={canvasRef}
        style={{
          width: '100%',
          height: '100%',
          display: 'block'
        }}
      />
    </div>
  );
}

export default SplashCursor;
