import React, { useEffect, useRef } from 'react';

function SplashCursor() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Canvas boyutunu ayarla
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    const ctx = canvas.getContext('2d');
    let mouseX = window.innerWidth / 2;
    let mouseY = window.innerHeight / 2;
    let particles = [];

    const handleMouseMove = (e) => {
      mouseX = e.clientX;
      mouseY = e.clientY;
      
      // Mouse hareket edince yeni partikÃ¼l ekle
      for (let i = 0; i < 3; i++) {
        particles.push({
          x: mouseX,
          y: mouseY,
          // PartikÃ¼l boyutu kÃ¼Ã§Ã¼ltÃ¼ldÃ¼
          size: Math.random() * 3 + 1, 
          // HÄ±z artÄ±rÄ±ldÄ± (daha hÄ±zlÄ± daÄŸÄ±lÄ±r)
          speedX: Math.random() * 10 - 5, 
          speedY: Math.random() * 10 - 5,
          color: `hsl(${Math.random() * 360}, 100%, 50%)`,
          // Ã–mÃ¼r kÄ±saltÄ±ldÄ± (daha Ã§abuk kaybolur)
          life: 25 
        });
      }
    };

    const animate = () => {
      // ðŸ’¡ TEMÄ°ZLEME Ã‡Ã–ZÃœMÃœ: YarÄ± ÅŸeffaf temizleme ile izlerin birikmesi engellenir.
      // Arka planÄ±nÄ±z beyazsa bu ayar izleri hafifÃ§e silerken iÃ§eriÄŸi kapatmaz.
      // OpaklÄ±k (0.15), kalÄ±cÄ±lÄ±ÄŸÄ± Ã¶nlemek iÃ§in yeterlidir.
      ctx.fillStyle = 'rgba(255, 255, 255, 0.15)'; 
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Ana cursor (Beyaz, boyutu kÃ¼Ã§Ã¼ltÃ¼ldÃ¼)
      ctx.fillStyle = '#ffffff';
      ctx.beginPath();
      ctx.arc(mouseX, mouseY, 6, 0, Math.PI * 2);
      ctx.fill();

      ctx.fillStyle = '#ffffff';
      ctx.beginPath();
      ctx.arc(mouseX, mouseY, 3, 0, Math.PI * 2);
      ctx.fill();

      // PartikÃ¼lleri Ã§iz ve gÃ¼ncelle
      for (let i = 0; i < particles.length; i++) {
        const p = particles[i];
        
        p.x += p.speedX;
        p.y += p.speedY;
        p.life--;

        // alpha deÄŸeri, yeni life: 25'e gÃ¶re hesaplanÄ±r
        const alpha = p.life / 25; 
        ctx.fillStyle = p.color.replace(')', `, ${alpha})`).replace('hsl', 'hsla');
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
        ctx.fill();

        if (p.life <= 0) {
          particles.splice(i, 1);
          i--;
        }
      }

      requestAnimationFrame(animate);
    };

    window.addEventListener('mousemove', handleMouseMove);
    animate();

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', resizeCanvas);
    };
  }, []);

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      // En Ã¼st katmanda kalmasÄ± iÃ§in zIndex 9999'da tutulur
      zIndex: 9999,
      // Alttaki linklere/butonlara tÄ±klanabilmesi iÃ§in bu Ã¶nemlidir
      pointerEvents: 'none', 
      width: '100%',
      height: '100%'
    }}>
      <canvas 
        ref={canvasRef}
        style={{
          width: '100%',
          height: '100%',
          display: 'block'
        }}
      />
    </div>
  );
}

export default SplashCursor;
